import React, {useState} from 'react'
import Axios from "axios";
import './welcome.css'

export default function Welcome(){
    const [username, setUsername] = useState('')
    // const [numPlayers, setNumPlayers] = useState(0)
    const register = (event) => {
        event.preventDefault()
        Axios.post("http://localhost:8000/welcome",{
            username: username,
        }).then((res) => {
            console.log(res);
            window.alert("Welcome "+username)

            Axios.get("http://localhost:8000/players").then(x =>{
                const numPlayers = ((x.data)[0])
                if ((Object.values(numPlayers)[0]) %2 == 0){
                    // init new game
                    Axios.post("http://localhost:8000/newgame").then(x =>{
                        console.log('game created')
                    })
                    window.location.href='/choose'
                }
                else{
                    window.location.href='/guess'
                }
            })
        });
    }

    return(
        <div className="regis">
            <div className={"backg"}>
        <span>
            Welcome mate!
        </span>
            <p/>

        <form onSubmit={register}>
            <label>Enter your name & press play</label>
                <p></p>
            <input className="spa" type={"text"}
                   required
                   minLength={1}
                   maxLength={10}
                   onChange={(e) => {
                       setUsername(e.target.value);
                   }}
            />

            <p/>
            <button className="sub" >Play!</button>

        </form>

            </div>
        </div>
    )
}
