import React, {useEffect, useRef, useState} from 'react'
import Axios from "axios";
import './draw.css'
import {render} from "react-dom";
import {wait} from "@testing-library/user-event/dist/utils";

import Guess from '../guess/Guess'
export default function Draw(props){
    let drawings = []
    const [isDrawUp, setIsDrawUp] = useState(false)
    const [message, setMessage] = useState('')

    const [isUserDraw,setIsUserDraw] = useState(false)
    const canvasRef = useRef(null)
    const contextRef = useRef(null)
    const [sent,setSent] = useState(false)
    const [isDrawing,setIsDrawing] = useState(false)
    const [drawList, setDrawList] = useState([])
    const[paths,setPaths] = useState(0)
    const [choosedWord,setChoosenWord] = useState('')
    const [prefix,setPrefix] = useState('')
    const [correctWord,setCorrectWord] = useState('')
    const [guessedWord,setGuessedWord] = useState('')

    useEffect(() =>{

        setIsUserDraw(props.isDrawer)
        const urlpath = window.location.href.split(("/"))
        // console.log(urlpath[urlpath.length-1])
        if(urlpath[urlpath.length-1] === 'drawt') setIsUserDraw(true)
        else setIsUserDraw(false)
        console.log(urlpath[urlpath.length-1] === 'drawt')
        const canvas = canvasRef.current;
        // canvas.width = (window.innerWidth*2);
        // canvas.height = (window.innerHeight*2);
        // canvas.style.height = `${window.innerHeight}px`;
        // canvas.style.width = `${window.innerWidth}px`;
        canvas.width  = 800;
        canvas.height = 600;
        canvas.style.width  = '400px';
        canvas.style.height = '300px';
        const context = canvas.getContext("2d")
        context.scale(2,2);
        context.lineCap = "round"
        context.strokeStyle="black"
        context.lineWidth = 5;
        contextRef.current = context;

        Axios.get("http://localhost:8000/getword").then(res =>{
            setChoosenWord(Object.values(res.data[0])[1])
        })

    },[])

    const startDrawing = ({nativeEvent}) => {
        const{offsetX,offsetY} = nativeEvent;
        contextRef.current.beginPath()
        contextRef.current.moveTo(offsetX,offsetY)
        setIsDrawing(true)
    }

     const finishDrawing = () => {
        contextRef.current.closePath()
        setIsDrawing(false)
        setPaths(paths+1)
        console.log(paths)
        console.log(drawList)

    }

    const send = () =>{
        setSent(true)
        console.log('sending draw')
        Axios.post("http://localhost:8000/draw",{
            drawings: drawList,
            paths: paths
        }).then((res) => {
        });

    }

    const clear = () =>{
        console.log('clearing draw')
                // DROP columns in drawings table.
                Axios.post('http://localhost:8000/deletepaths',
                    {} // don't need to send data
                ).then((res) =>{
                    console.log('paths deleted')
                })
        window.location.reload();


    }

    const draw = ({nativeEvent}) => {
        if(!isDrawing){
            return
        }
        const {offsetX,offsetY} = nativeEvent;
        drawList.push([paths,offsetX,offsetY]);
        contextRef.current.lineTo(offsetX,offsetY);
        contextRef.current.stroke();
    }

    return(
        <div className={"draw"}>
            <p className={"draw"}>Draw {prefix} {choosedWord}!</p>
            <canvas className={"canvas"}
            onMouseDown={startDrawing}
            onMouseUp={finishDrawing}
            onMouseMove={draw}
            ref={canvasRef}/>
            <p/>
                <div>
                <button type="text" onClick={send}>Send</button>
                <button type="text" onClick={clear}>Clear</button>
                </div>

            {/*{!isUserDraw ?*/}
            {/*    <div className={"guess"}>*/}
            {/*        Guess the word*/}
            {/*        <p/>*/}
            {/*        /!*<canvas className={"canvas"}*!/*/}
            {/*        /!*        ref={canvasRef}/>*!/*/}
            {/*        /!*<p/>*!/*/}
            {/*        <p/>*/}
            {/*        /!*<form onSubmit={guess}>*!/*/}
            {/*        <label>Enter your guess</label>*/}
            {/*        <p></p>*/}
            {/*        <input className="guessedWord" type={"text"}*/}
            {/*               required*/}
            {/*               minLength={1}*/}
            {/*               maxLength={20}*/}
            {/*               onChange={(e) => {*/}
            {/*                   setGuessedWord(e.target.value);*/}
            {/*               }}*/}
            {/*        />*/}
            {/*        <p/>*/}
            {/*        <button className="sub" onClick={guess}>Submit</button>*/}
            {/*        /!*</form>*!/*/}
            {/*        {message.toString()!=='' ? <p>{message}</p> : <p/>}*/}
            {/*        /!*create hints as a feature i.e. number of letters,...*!/*/}
            {/*    </div>*/}
            {/*    : <p/>}*/}
        </div>
    )
}
