import React, {useState} from 'react'
import './choose.css'
import Axios from "axios";

export default function Choose(){
    const randomWords = require('random-words');
    const [name,setName] = useState('');
    const [points,setPoints] = useState(0)
    let words = randomWords(3);

    const chosenWord = (name,points) => {
        Axios.post("http://localhost:8000/choose", {
            name: name,
            points:points
        }).then((res) => {
            console.log(res)
            window.alert('You choose the word '+name+'!');
            window.location.href= "/draw";
            })
    }
    return(
        <div className="play">
            <p>
                So, we are about to play!
            </p>
            <p>
                Please choose one of the following words:
            </p>
            <table className="wordsView">
                <thead>
                <tr>
                    <td className="word" onClick={(e) => {
                        chosenWord(words[0],1)
                    }}>
                        {words[0]}
                    </td>
                    <td className="word" onClick={() => {
                        chosenWord(words[1],3)
                    }}>
                        {words[1]}
                    </td>
                    <td className="word" onClick={() => {
                        chosenWord(words[2],points)
                    }}>
                        {words[2]}
                    </td>
                </tr>
                </thead>
            </table>
        </div>
    )
}
