import React, {useState} from 'react'
import './choose.css'
import Axios from "axios";

export default function Choose(){
    const randomWords = require('random-words');
    const [word,setWord] = useState('');
    const [points,setPoints] = useState(0)
    let words = randomWords(3);

    const choosenWord = () => {
        Axios.post("http://localhost:8000/choose", {
            name: word,
            points:points
        }).then((res) => {
            console.log(res)
            window.alert('You choose the word '+word+'!');
            window.location.href= "/draw";

            })
    }

    return(
        <div className="play">
            <p>
                So, we are about to play!
            </p>
            <p>
                Please choose one of the following words:
            </p>
            <table className="wordsView">
                <tr>
                    <td className="word" onClick={() => {
                        setWord(words[0])
                        setPoints(1)

                    }}>
                        {words[0]}
                    </td>
                    <td className="word" onClick={() => {
                        setWord(words[1])
                        setPoints(3)
                    }}>
                        {words[1]}
                    </td>
                    <td className="word" onClick={() => {
                        setWord(words[2])
                        setPoints(5)
                    }}>
                        {words[2]}
                    </td>
                </tr>
            </table>
        </div>
    )
}
