import React, {useEffect, useRef, useState} from 'react'
import './guess.css'
import Axios from "axios";
import {wait} from "@testing-library/user-event/dist/utils";
export default function Guess(){
    let drawings = []
    let isActivate = false;
    const getDrawings = () =>{
        isActivate= true;
        Axios.get("http://localhost:8000/getdraw").then((x)=>{
            const pathLength = (x.data).length
            for(let iterator=0; iterator<pathLength; iterator++){
                drawings[iterator] = [Object.values(x.data[iterator])[1],Object.values(x.data[iterator])[2]]
            }
        })
        isActivate=false;

    }
    const canvasRef = useRef(null)
    const contextRef = useRef(null)

    useEffect(() =>{
        const canvas = canvasRef.current;
        // canvas.width = (window.innerWidth*2);
        // canvas.height = (window.innerHeight*2);
        // canvas.style.height = `${window.innerHeight}px`;
        // canvas.style.width = `${window.innerWidth}px`;
        canvas.width  = 800;
        canvas.height = 600;
        canvas.style.width  = '400px';
        canvas.style.height = '300px';
        const context = canvas.getContext("2d")
        context.scale(2,2);
        context.lineCap = "round"
        context.strokeStyle="black"
        context.lineWidth = 5;
        contextRef.current = context;

    },[])

    const draw = () => {
        getDrawings();
        wait(2000).then(r => {
            console.log('reached')

            for(let i=0;i<drawings.length;i++){
                contextRef.current.lineTo(drawings[i][0],drawings[i][1]);
                console.log(drawings[i])
                contextRef.current.stroke();
            }
        })


        }


    return(
        <div>
            GUESS
            <canvas className={"canvas"}
                    ref={canvasRef}/>
            <button type="text" onClick={draw}> GET DRAWINGS</button>
        </div>
    )
}
