import React, {useState} from 'react'
import Axios from "axios";
import './welcome.css'

export default function Welcome(){
    const [username, setUsername] = useState('')
    function convertIntObj(obj) {
        const res = {}
        for (const key in obj) {
            res[key] = {};
            for (const prop in obj[key]) {
                const parsed = parseInt(obj[key][prop], 10);
                res[key][prop] = isNaN(parsed) ? obj[key][prop] : parsed;
            }
        }
        return res;
    }
    const register = (event) => {
        event.preventDefault()
        Axios.post("http://localhost:8000/welcome",{
            username: username,
        }).then((res) => {
            console.log(res);
            // window.alert("Welcome "+username)

            Axios.get("http://localhost:8000/players").then(x =>{
                let numPlayers = ((x.data)[0])
                console.log(Object.values(numPlayers))
            })

        });
    }

    return(
        <div className="regis">
        <span>
            Welcome mate!
        </span>
            <p/>

        <form onSubmit={register}>
            <label>Enter your name</label>
                <p></p>
            <input className="spa" type={"text"}
                   required
                   minLength={1}
                   maxLength={10}
                   onChange={(e) => {
                       setUsername(e.target.value);
                   }}
            />

            <p/>
            <button className="sub" >Play!</button>

        </form>


        </div>
    )
}
